import {useNavigate, useNavigation, useActionData, Form, json, redirect} from 'react-router-dom';
import classes from './EventForm.module.css';
import {getAuthToken} from "../util/auth";

function EventForm({ method, event }) {
  const data = useActionData();
  const navigate = useNavigate();
  const navigation = useNavigation();
  const isSubmitting = navigation.state === "submitting";
  function cancelHandler() {
    navigate('..');
  }

  return (
    <Form method={method} className={classes.form}>
        {data && data.errors && (<ul>
            {Object.values(data.errors).map(err => <li key={err}>{err}</li>)}
        </ul>)}
      <p>
        <label htmlFor="title">Title</label>
        <input id="title" type="text" name="title"  defaultValue={event ? event.title: ""} />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input id="image" type="url" name="image"  defaultValue={event ?event.image: ""} />
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input id="date" type="date" name="date" required defaultValue={event ? event.date: ""}/>
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea id="description" name="description" rows="5"  defaultValue={event ? event.description: ""} />
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler} disabled={isSubmitting}>
          Cancel
        </button>
        <button disabled={isSubmitting}>{isSubmitting ? "Submitting...": "Save"}</button>
      </div>
    </Form>
  );
}

export const action = async({request, params}) => {
    const token = getAuthToken()
    const method = request.method
    const data = await request.formData();
    const eventData = { title: data.get("title"), image: data.get("image"), date: data.get("date"), description: data.get('description') }
    let url = "http://localhost:8080/events";
    if (method === "PATCH"){
        const eventId = params.eventId;
        url = "http://localhost:8080/events/" +eventId ;
    }
    const response = await fetch(url,
        {
            method: method,
            headers: { 'Content-Type': "application/json", "Authorization": "Bearer "+ token},
            body:JSON.stringify(eventData)
        })
    if (response.status === 422){
        return response;
    }
    if(!response.ok){
        throw json({massage: "Could not save event."}, {status: 500})
    }
    return redirect('/events');
}
export default EventForm;
