import React, {useEffect} from "react";
import MainNavigation from "../components/MainNavigation";
import {Outlet, useLoaderData} from "react-router-dom";

const RootLayout = () => {
    const token = useLoaderData();
    
    useEffect(()=>{
        if (!token){
            return;
        }
        setTimeout(()=> {}, timeout)
    },[token])
    return <>
    <MainNavigation/>
    <main>
        <Outlet/>
    </main>
    </>
}
export default RootLayout;
