import React, {useState} from "react";
import "./App.css"

export default function App(){
    const [step, setStep] = useState(1)

    return(
        <div className="container">
            <div className="steps">
                <button>-</button>
                <span>Step {step}</span>
                <button>+</button>
            </div>
            <div className="count"></div>
        </div>
    )
}
