import React, {useState} from "react";
import "./App.css"

export default function App(){
    const [step, setStep] = useState(1)
    let count = 1;
    let result = new Date().toDateString()
    return(
        <div className="container">
            <div className="step">
                <button>-</button>
                <span>Step: {step}</span>
                <button>+</button>
            </div>
            <div className="count">
                <button>-</button>
                <span>Count: {count}</span>
                <button>+</button>
            </div>
            <div className="result">{result}</div>
        </div>
    )
}
