import React from "react"
import ReactDOM from "react-dom/client"
import "./index.css"
const pizzaData = [
    {
        name: "Focaccia",
        ingredients: "Bread with italian olive oil and rosemary",
        price: 6,
        photoName: "pizzas/focaccia.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Margherita",
        ingredients: "Tomato and mozarella",
        price: 10,
        photoName: "pizzas/margherita.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Spinaci",
        ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
        price: 12,
        photoName: "pizzas/spinaci.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Funghi",
        ingredients: "Tomato, mozarella, mushrooms, and onion",
        price: 12,
        photoName: "pizzas/funghi.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Salamino",
        ingredients: "Tomato, mozarella, and pepperoni",
        price: 15,
        photoName: "pizzas/salamino.jpg",
        soldOut: true,
    },
    {
        name: "Pizza Prosciutto",
        ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
        price: 18,
        photoName: "pizzas/prosciutto.jpg",
        soldOut: false,
    },
];

function App(){
    return (
        <div className={"container"}>
            <Header/>
            <Menu/>
            <Footer/>
        </div>)
}
function Header(){
    // const style= { color:"red", fontSize: "48px", textTransform:"uppercase" }
    const style = {};
    return(
        <header className={'header'}>
            <h1 style={style}>Fast React Pizza Co.</h1>
        </header>
        )
}
function Menu(){
    return(
        <main className={"menu"}>
            <h2>Our menu</h2>
            <ul className={"pizzas"}>
            {pizzaData.map(pizza => (
                <Pizza
                    key={pizza.name}
                    name={pizza.name}
                    ingredients={pizza.ingredients}
                    photoName={pizza.photoName}
                    price={pizza.price}
                />
            ))}
            </ul>
        </main>
    )
}
function Pizza(props){
    return (
        <li className={"pizza"}>
            <img src={props.photoName} alt={props.name}/>
            <div>
                <h3>{props.name}</h3>
                <p>{props.ingredients}</p>
                <span>{props.price}$</span>
            </div>
        </li>
    )
}
function Footer(){
    const hour = new Date().getHours();
    const openHour = 12 ;
    const closeHour = 15;
    const isOpen = hour >= openHour && hour<= closeHour
    let content = "";

    return (
        <footer className={"footer"}>
            {isOpen &&  <div className={'order'}>
                <p>We're open until {closeHour}:00. Come visit or order online</p>
                <button className="btn">Order</button>
            </div> }
            {!isOpen && <div className={'order'}>
                <p>We open at {openHour}:00. Come visit or order online later</p>
                <button className="btn" disabled={true}>Order</button>
            </div>}

        </footer>
    )
    // return React.createElement('footer', null, "We're currently open!")
}

// React v18
const root = ReactDOM.createRoot(document.getElementById("root"));
// react strict mode check if react code is in newer version
root.render(
    <React.StrictMode>
        <App/>
    </React.StrictMode>)

// React before 18
// import ReactDOM from "react-dom"
// React.render(<App/>)
