import React from "react"
import ReactDOM from "react-dom/client"
import "./index.css"
const pizzaData = [
    {
        name: "Focaccia",
        ingredients: "Bread with italian olive oil and rosemary",
        price: 6,
        photoName: "pizzas/focaccia.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Margherita",
        ingredients: "Tomato and mozarella",
        price: 10,
        photoName: "pizzas/margherita.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Spinaci",
        ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
        price: 12,
        photoName: "pizzas/spinaci.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Funghi",
        ingredients: "Tomato, mozarella, mushrooms, and onion",
        price: 12,
        photoName: "pizzas/funghi.jpg",
        soldOut: false,
    },
    {
        name: "Pizza Salamino",
        ingredients: "Tomato, mozarella, and pepperoni",
        price: 15,
        photoName: "pizzas/salamino.jpg",
        soldOut: true,
    },
    {
        name: "Pizza Prosciutto",
        ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
        price: 18,
        photoName: "pizzas/prosciutto.jpg",
        soldOut: false,
    },
];

function App(){
    return (
        <div className={"container"}>
            <Header/>
            <Menu/>
            <Footer/>
        </div>)
}
function Header(){
    // const style= { color:"red", fontSize: "48px", textTransform:"uppercase" }
    const style = {};
    return(
        <header className={'header'}>
            <h1 style={style}>Fast React Pizza Co.</h1>
        </header>
        )
}
function Menu(){
    return(
        <div className={"menu"}>
            <h2>Our menu</h2>
            <Pizza/>
            <Pizza/>
            <Pizza/>
            <Pizza/>
        </div>
    )
}
function Footer(){
    const hour = new Date().getHours();
    const openHour = 12 ;
    const closeHour = 22;
    const isOpen = hour >= openHour && hour<= closeHour
    let content = "";
    if (isOpen){
        content = "We're currently open!"
    }else{
        content = "Sorry we're closed"
    }
    return (
        <footer>{new Date().toLocaleTimeString()}, {content}</footer>
    )
    // return React.createElement('footer', null, "We're currently open!")
}
function Pizza(){
    return (
        <div>
            <img src="pizzas/focaccia.jpg" alt="Focaccia pizza"/>
            <h2>Focaccia</h2>
            <p>Bread with italian olive oil and rosemary</p>
            <p>6$</p>
        </div>
    )
}
// React v18
const root = ReactDOM.createRoot(document.getElementById("root"));
// react strict mode check if react code is in newer version
root.render(
    <React.StrictMode>
        <App/>
    </React.StrictMode>)

// React before 18
